

window.addEventListener("scroll", () => {
    const checkpoint = 300;
    const currentScroll = window.pageYOffset;
    if (currentScroll <= checkpoint) {
        opacity = 1 - currentScroll / checkpoint;
    } else {
        opacity = 0;
    }
    document.querySelector(".front").style.opacity = opacity;

});

window.onscroll = function () {
    const menu = document.querySelector('.navbar-group');
    const distancia = window.innerHeight - menu.getBoundingClientRect().top;
    if (distancia >= 300) {
        menu.classList.add('navbar-show');
    } else {
        menu.classList.remove('navbar-show');
    }
};

window.onload = function(){
function loadBio() {
    const btnDropdown = document.getElementById('btnDropdown');
    const bioDropdown = document.querySelector('.bioDropdown');
    btnDropdown.addEventListener('click', () => {
        
        if(bioDropdown.classList.contains('show')){
            bioDropdown.classList.add('hide');
            bioDropdown.classList.remove('show');
            setTimeout(()=>{
                bioDropdown.classList.remove('hide');
                bioDropdown.style.display='none';
                btnDropdown.textContent="Ver Más";
            },1000);
            return;
        }else{
            bioDropdown.classList.add('show');
            bioDropdown.style.display = 'block';
            btnDropdown.textContent="Ver Menos";
        }
    });
}

const btnVideo = document.getElementById('btnVideo');
const videoDropdown = document.querySelector('.videoDropdown');
function loadVideos(){
    btnVideo.addEventListener('click',()=>{
        if(videoDropdown.classList.contains('show')){
            videoDropdown.classList.add('hide');
            videoDropdown.classList.remove('show');
            setTimeout(()=>{
                videoDropdown.classList.remove('hide');
                videoDropdown.style.display='none';
                btnVideo.textContent="Ver Más";
            },1000);
        }else{
            videoDropdown.classList.add('show');
            videoDropdown.style.display='block';
            btnVideo.textContent="Ver Menos";
        }
    });
}


var contenedor = document.getElementById('contenedor_carga');
contenedor.style.visibility='hidden';
contenedor.style.opacity='0';


loadVideos();
loadBio();


}






